#define BANNER_PICT_ID 128#define STOP_ALRT_ID 128short quitting = 0;void InitToolbox(void);void draw_window(void);void InitToolbox(){	InitGraf((Ptr) &qd.thePort);	InitFonts();	InitWindows();	InitMenus();	FlushEvents(everyEvent,0);	TEInit();	InitDialogs(0L);	InitCursor();}main(){	Rect win_bounds = { 50, 30, 0, 0 };	WindowPtr win;	Str255 title = "\pUgra Classic";		InitToolbox();	InitGraf(&thePort);	FlushEvents(everyEvent, 0);	InitWindows();	InitMenus();	TEInit();	InitDialogs(0);	InitCursor();	MaxApplZone();		win_bounds.right = 305;	win_bounds.bottom = 245;		win = NewWindow(0L, &win_bounds, title, true,		altDBoxProc, (WindowPtr) - 1L, true, 0);	if (!win) {		ExitToShell();	}			SetPort(win);	draw_window();	while (!quitting) { 		EventRecord event;		WindowPtr event_win;		short event_in;				WaitNextEvent(everyEvent, &event, 10L, 0);		event_in = FindWindow(event.where, &event_win);				switch (event.what) {		case mouseDown:			switch (event_in) {			case inSysWindow:				SystemClick(&event, event_win);				break;			case inDrag:				DragWindow(event_win, event.where, &screenBits.bounds);				break;			case inGoAway:				if (TrackGoAway(event_win, event.where))					quitting = 1;				break;			}			break;		case mouseUp:			switch (event_in) {			case inContent:				quitting = Alert(STOP_ALRT_ID, 0);				break;			}			break;		case updateEvt:			BeginUpdate((WindowPtr)event.message);			draw_window();			EndUpdate((WindowPtr)event.message);			break;		}	}		ExitToShell();}voiddraw_window(){	PicHandle banner;	Rect banner_rect = { 0 };	banner = GetPicture(BANNER_PICT_ID);	if (!banner) {		quitting = 1;		return;	}	banner_rect.left = (**(banner)).picFrame.left;	banner_rect.right = (**(banner)).picFrame.right;	banner_rect.bottom = (**(banner)).picFrame.bottom;	DrawPicture(banner, &banner_rect);		MoveTo(4, 138);	TextFont(1);	TextSize(9);	TextFace(0);	DrawString("\pProgramma dlya uchyota i provedenia meropriyatij.");	MoveTo(4, 151);	TextFace(1);	DrawString("\pIspol'zovanie 3 licami VOSPRESHCHAETSYA!!!");	MoveTo(4, 163);	TextFace(0);	DrawString("\pV programme est sekrety.");	MoveTo(131, 190);	TextFace(0);	DrawString("\p(c) 2023 . XAHTbI-MAHCU`UCK");		MoveTo(190, 12);	TextFont(0);	TextSize(12);	TextFace(0);	DrawString("\p3AGPY3KA...");}